<?phpclass Mec_Pointsaddon_Block_Checkout_Cart_Item_Configure extends Mage_Checkout_Block_Cart_Item_Configure{	protected function _prepareLayout()    {		Mage::log('_prepareLayout');        // Set custom submit url route for form - to submit updated options to cart        $block = $this->getLayout()->getBlock('product.info');        if ($block) {             $block->setSubmitRouteData(array(                'route' => 'checkout/cart/updateItemOptions',                'params' => array('id' => $this->getRequest()->getParam('id'))             ));        }        // Set custom template with 'Update Cart' button        $block = $this->getLayout()->getBlock('product.info.addtocart');        if ($block) {            $block->setTemplate('mec/checkout/cart/item/configure/updatecart.phtml');        }        // return parent::_prepareLayout();    }}